package com.itcast.service.impl;import com.itcast.pojo.User;import com.itcast.service.UserMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by zhangfajie on 2020/5/28. */@Servicepublic class UserServiceImpl implements UserMapper {    @Autowired    private UserMapper userMapper;    @Override    public User finId(Integer userid) {        return userMapper.finId(userid);    }    @Override    public List<User> finId_1() {        return userMapper.finId_1();    }    @Override    public List<User> finAll() {        return userMapper.finAll();    }}